{
  "name": "Simple Welcome Journey",
  "start_node_id": "welcome",
  "nodes": [
    {
      "id": "welcome",
      "type": "MESSAGE",
      "message": "Welcome to your care journey!",
      "next_node_id": "check_age"
    },
    {
      "id": "check_age",
      "type": "CONDITIONAL",
      "condition": {
        "field": "age",
        "operator": ">",
        "value": 65
      },
      "on_true_next_node_id": "senior_message",
      "on_false_next_node_id": "general_message"
    },
    {
      "id": "senior_message",
      "type": "MESSAGE",
      "message": "As a senior patient, you have access to specialized care programs.",
      "next_node_id": null
    },
    {
      "id": "general_message",
      "type": "MESSAGE",
      "message": "Thank you for choosing our care program!",
      "next_node_id": null
    }
  ]
}

{
  "name": "Orthopedic Surgery Recovery Journey",
  "start_node_id": "check_language",
  "nodes": [
    {
      "id": "check_language",
      "type": "CONDITIONAL",
      "condition": {
        "field": "language",
        "operator": "=",
        "value": "es"
      },
      "on_true_next_node_id": "welcome_spanish",
      "on_false_next_node_id": "welcome_english"
    },
    {
      "id": "welcome_english",
      "type": "MESSAGE",
      "message": "Welcome to your personalized recovery program. We're here to support your healing journey.",
      "next_node_id": "processing_delay"
    },
    {
      "id": "welcome_spanish",
      "type": "MESSAGE",
      "message": "Bienvenido a su programa de recuperación personalizado. Estamos aquí para apoyar su proceso de sanación.",
      "next_node_id": "processing_delay"
    },
    {
      "id": "processing_delay",
      "type": "DELAY",
      "delay_seconds": 2,
      "next_node_id": "check_surgery_type"
    },
    {
      "id": "check_surgery_type",
      "type": "CONDITIONAL",
      "condition": {
        "field": "condition",
        "operator": "=",
        "value": "hip_replacement"
      },
      "on_true_next_node_id": "hip_replacement_path",
      "on_false_next_node_id": "knee_replacement_path"
    },
    {
      "id": "hip_replacement_path",
      "type": "CONDITIONAL",
      "condition": {
        "field": "language",
        "operator": "=",
        "value": "es"
      },
      "on_true_next_node_id": "hip_instructions_spanish",
      "on_false_next_node_id": "hip_instructions_english"
    },
    {
      "id": "hip_instructions_english",
      "type": "MESSAGE",
      "message": "Hip replacement recovery: Avoid bending your hip more than 90 degrees. Use your walker for the first 6 weeks. Physical therapy starts in 2-3 days.",
      "next_node_id": "recovery_delay"
    },
    {
      "id": "hip_instructions_spanish",
      "type": "MESSAGE",
      "message": "Recuperación de reemplazo de cadera: Evite doblar la cadera más de 90 grados. Use su andador durante las primeras 6 semanas. La fisioterapia comenzará en 2-3 días.",
      "next_node_id": "recovery_delay"
    },
    {
      "id": "knee_replacement_path",
      "type": "CONDITIONAL",
      "condition": {
        "field": "language",
        "operator": "=",
        "value": "es"
      },
      "on_true_next_node_id": "knee_instructions_spanish",
      "on_false_next_node_id": "knee_instructions_english"
    },
    {
      "id": "knee_instructions_english",
      "type": "MESSAGE",
      "message": "Knee replacement recovery: Keep your leg elevated when resting. Do your ankle pumps every hour. Ice for 20 minutes, 3 times daily.",
      "next_node_id": "recovery_delay"
    },
    {
      "id": "knee_instructions_spanish",
      "type": "MESSAGE",
      "message": "Recuperación de reemplazo de rodilla: Mantenga la pierna elevada al descansar. Haga ejercicios de tobillo cada hora. Aplique hielo por 20 minutos, 3 veces al día.",
      "next_node_id": "recovery_delay"
    },
    {
      "id": "recovery_delay",
      "type": "DELAY",
      "delay_seconds": 20,
      "next_node_id": "follow_up_message"
    },
    {
      "id": "follow_up_message",
      "type": "CONDITIONAL",
      "condition": {
        "field": "language",
        "operator": "=",
        "value": "es"
      },
      "on_true_next_node_id": "follow_up_spanish",
      "on_false_next_node_id": "follow_up_english"
    },
    {
      "id": "follow_up_english",
      "type": "MESSAGE",
      "message": "Remember: Take your pain medication as prescribed and call us immediately if you experience fever, increased swelling, or severe pain.",
      "next_node_id": null
    },
    {
      "id": "follow_up_spanish",
      "type": "MESSAGE",
      "message": "Recuerde: Tome su medicamento para el dolor según lo recetado y llámenos inmediatamente si experimenta fiebre, hinchazón aumentada o dolor severo.",
      "next_node_id": null
    }
  ]
}
